mapper.select:
SELECT EDU_TEACHER.ID, EDU_TEACHER.NAME, EDU_TEACHER.EDUCATION, EDU_TEACHER.CAREER, EDU_TEACHER.IS_STAR, EDU_TEACHER.PIC_PATH, EDU_TEACHER.STATUS, date_format(EDU_TEACHER.CREATE_TIME,'%Y/%m/%d') CREATE_TIME, EDU_TEACHER.UPDATE_TIME, EDU_TEACHER.SUBJECT_ID, EDU_TEACHER.SORT FROM EDU_TEACHER left join sys_subject on EDU_TEACHER.SUBJECT_ID = sys_subject.subject_id WHERE EDU_TEACHER.STATUS=0
and EDU_TEACHER.NAME=#{name}
and EDU_TEACHER.IS_STAR=#{is_star}
and EDU_TEACHER.CREATE_TIME >#{start_time} and #{end_time}>EDU_TEACHER.CREATE_TIME
ORDER BY EDU_TEACHER.SORT desc

SELECT EDU_TEACHER.ID, EDU_TEACHER.NAME, EDU_TEACHER.EDUCATION, EDU_TEACHER.CAREER, EDU_TEACHER.IS_STAR, EDU_TEACHER.PIC_PATH, EDU_TEACHER.STATUS, EDU_TEACHER.CREATE_TIME, EDU_TEACHER.UPDATE_TIME, EDU_TEACHER.SUBJECT_ID, EDU_TEACHER.SORT 
FROM EDU_TEACHER 
WHERE EDU_TEACHER.ID = #{id}

mapper.update:
UPDATE EDU_TEACHER 
SET EDU_TEACHER.NAME = #{NAMET}, EDU_TEACHER.EDUCATION = #{EDUCATION}, EDU_TEACHER.CAREER = #{CAREER}, EDU_TEACHER.IS_STAR = #{IS_STAR}, EDU_TEACHER.PIC_PATH = #{PIC_PATH}, EDU_TEACHER.UPDATE_TIME = NOW(), EDU_TEACHER.SUBJECT_ID =#{SUBJECT_ID}, EDU_TEACHER.SORT=#{SORT} 
WHERE EDU_TEACHER.ID = #{ID}
